$(document).ready(function(){var t=250,o=300;$(window).scroll(function(){$(this).scrollTop()>t?$(".top").fadeIn(500):$(".top").fadeOut(500)}),$(".top").click(function(t){t.preventDefault(),$("html, body").animate({scrollTop:0},o)})}),$(function(){$("a[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")===this.pathname.replace(/^\//,"")&&location.hostname===this.hostname){var t=$(this.hash);if(t=t.length?t:$("[name="+this.hash.slice(1)+"]"),t.length)return $("html,body").animate({scrollTop:t.offset().top},1e3),!1}})});